mapper.select:
SELECT


,yaopin.yaopin_name as yaopinName
,yaopin.yaopin_uuid_number as yaopinUuidNumber
,yaopin.yaopin_photo as yaopinPhoto
,yaopin.zan_number as zanNumber
,yaopin.cai_number as caiNumber
,yaopin.yaopin_types as yaopinTypes
,yaopin.yaopin_kucun_number as yaopinKucunNumber
,yaopin.yaopin_old_money as yaopinOldMoney
,yaopin.yaopin_new_money as yaopinNewMoney
,yaopin.yaopin_clicknum as yaopinClicknum
,yaopin.yaopin_content as yaopinContent
,yaopin.shangxia_types as shangxiaTypes
,yaopin.yaopin_delete as yaopinDelete
,yonghu.yonghu_name as yonghuName
,yonghu.yonghu_phone as yonghuPhone
,yonghu.yonghu_id_number as yonghuIdNumber
,yonghu.yonghu_photo as yonghuPhoto
,yonghu.yonghu_email as yonghuEmail
,yonghu.new_money as newMoney
,yonghu.yonghu_delete as yonghuDelete

FROM cart  a
left JOIN yaopin yaopin ON a.yaopin_id = yaopin.id
left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id



        and a.id in

        #{item}



        and (
            a.yonghu_id = #{params.yonghuId}
        )


        and (
            a.yaopin_id = #{params.yaopinId}
        )


          and a.buy_number >= #{params.buyNumberStart}   


          and a.buy_number <= #{params.buyNumberEnd}   


        and a.buy_number = #{params.buyNumber}


          and UNIX_TIMESTAMP(a.update_time) >= UNIX_TIMESTAMP(#{params.updateTimeStart}) 


          and UNIX_TIMESTAMP(a.update_time) <= UNIX_TIMESTAMP(#{params.updateTimeEnd}) 


          and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 




        and a.yaopin_id IS NOT NULL


        and yaopin.yaopin_name like CONCAT('%',#{params.yaopinName},'%')


        and yaopin.yaopin_uuid_number like CONCAT('%',#{params.yaopinUuidNumber},'%')


          and yaopin.zan_number >= #{params.zanNumberStart}   


          and yaopin.zan_number <= #{params.zanNumberEnd}   


        and yaopin.zan_number = #{params.zanNumber}


          and yaopin.cai_number >= #{params.caiNumberStart}   


          and yaopin.cai_number <= #{params.caiNumberEnd}   


        and yaopin.cai_number = #{params.caiNumber}


        and yaopin.yaopin_types = #{params.yaopinTypes}



          and yaopin.yaopin_kucun_number >= #{params.yaopinKucunNumberStart}   


          and yaopin.yaopin_kucun_number <= #{params.yaopinKucunNumberEnd}   


        and yaopin.yaopin_kucun_number = #{params.yaopinKucunNumber}


          and yaopin.yaopin_old_money >= #{params.yaopinOldMoneyStart}   


          and yaopin.yaopin_old_money <= #{params.yaopinOldMoneyEnd}   


          and yaopin.yaopin_new_money >= #{params.yaopinNewMoneyStart}   


          and yaopin.yaopin_new_money <= #{params.yaopinNewMoneyEnd}   


          and yaopin.yaopin_clicknum >= #{params.yaopinClicknumStart}   


          and yaopin.yaopin_clicknum <= #{params.yaopinClicknumEnd}   


        and yaopin.yaopin_clicknum = #{params.yaopinClicknum}


        and yaopin.yaopin_content like CONCAT('%',#{params.yaopinContent},'%')


        and yaopin.shangxia_types = #{params.shangxiaTypes}



          and yaopin.yaopin_delete >= #{params.yaopinDeleteStart}   


          and yaopin.yaopin_delete <= #{params.yaopinDeleteEnd}   


        and yaopin.yaopin_delete = #{params.yaopinDelete}


          and UNIX_TIMESTAMP(yaopin.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(yaopin.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 



        and a.yonghu_id IS NOT NULL


        and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')


        and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')


        and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')


        and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')


          and yonghu.new_money >= #{params.newMoneyStart}   


          and yonghu.new_money <= #{params.newMoneyEnd}   


          and yonghu.yonghu_delete >= #{params.yonghuDeleteStart}   


          and yonghu.yonghu_delete <= #{params.yonghuDeleteEnd}   


        and yonghu.yonghu_delete = #{params.yonghuDelete}


          and UNIX_TIMESTAMP(yonghu.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(yonghu.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 


order by a.${params.sort} ${params.order}
